mapper.select:
select count(*)
from members 
where member_email = #{memberEmail}

mapper.update:
update members set
member_pw = #{memberPw}
where
member_id = #{memberId}

mapper.select:
select member_id, member_email, member_name, member_tel, member_addr1, member_addr2, member_authority,
member_postal, member_image, member_birth, member_color, doctor_room
from members where member_id = #{memberId} and hospital_code = #{hospitalCode}
